mapper.select:
select
id, user_id, oss_details, file_type, create_time, update_time
from work_platform.download_info


        and id = #{id}


        and user_id = #{userId}


        and oss_details = #{ossDetails}


        and file_type = #{fileType}


        and create_time = #{createTime}


        and update_time = #{updateTime}
